import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './ChatWidget.css';

const ChatWidget = ({ apiKey }) => {
  const [messages, setMessages] = useState([
    {
      content: "How can I help you today?",
      sender: 'bot'
    }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isOpen, setIsOpen] = useState(true);
  const messagesEndRef = useRef(null);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!input.trim()) return;
    
    const userMessage = {
      content: input,
      sender: 'user'
    };
    
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);
    
    try {
      const response = await axios.post('/chat', {
        message: input,
        api_key: apiKey
      });
      
      const botMessage = {
        content: response.data.response,
        sender: 'bot'
      };
      
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
      
      const errorMessage = {
        content: `Sorry, there was an error: ${error.response?.data?.detail || error.message || 'Unknown error'}`,
        sender: 'bot',
        isError: true
      };
      
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const toggleWidget = () => {
    setIsOpen(!isOpen);
  };

  // Initial chat bubble icon (when closed)
  const chatBubbleIcon = (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M12 2C6.48 2 2 6.48 2 12C2 17.52 6.48 22 12 22C17.52 22 22 17.52 22 12C22 6.48 17.52 2 12 2ZM16 13H13V16C13 16.55 12.55 17 12 17C11.45 17 11 16.55 11 16V13H8C7.45 13 7 12.55 7 12C7 11.45 7.45 11 8 11H11V8C11 7.45 11.45 7 12 7C12.55 7 13 7.45 13 8V11H16C16.55 11 17 11.45 17 12C17 12.55 16.55 13 16 13Z" fill="currentColor"/>
    </svg>
  );

  // Close icon (X)
  const closeIcon = (
    <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M14 1.41L12.59 0L7 5.59L1.41 0L0 1.41L5.59 7L0 12.59L1.41 14L7 8.41L12.59 14L14 12.59L8.41 7L14 1.41Z" fill="currentColor"/>
    </svg>
  );

  return (
    <div className={`chat-widget ${isOpen ? 'open' : 'closed'}`}>
      {isOpen ? (
        <>
          <div className="chat-widget-header">
            Chat with AI Assistant
          </div>
          <div className="chat-widget-messages">
            {messages.map((message, index) => (
              <div 
                key={index} 
                className={`chat-widget-message ${message.sender === 'user' ? 'user-message' : 'bot-message'}`}
              >
                <div className="chat-widget-message-content">
                  {message.sender === 'bot' 
                    ? message.content.split('\n').map((line, i) => (
                        <React.Fragment key={i}>
                          {line.trim().startsWith('â€¢') || line.trim().startsWith('-') || /^\d+[.)]/.test(line.trim()) 
                            ? <div className="point-item">{line}</div> 
                            : line
                          }
                          {i !== message.content.split('\n').length - 1 && <br />}
                        </React.Fragment>
                      ))
                    : message.content
                  }
                </div>
              </div>
            ))}
            
            {isLoading && (
              <div className="chat-widget-message bot-message">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            )}
            
            <div ref={messagesEndRef} />
          </div>
          <form className="chat-widget-input" onSubmit={handleSubmit}>
            <input
              type="text"
              value={input}
              onChange={handleInputChange}
              placeholder="Type your message..."
              disabled={isLoading}
            />
            <button type="submit" disabled={isLoading || !input.trim()}>
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M2.01 21L23 12L2.01 3L2 10L17 12L2 14L2.01 21Z" fill="currentColor"/>
              </svg>
            </button>
          </form>
        </>
      ) : (
        <div className="chat-widget-button">
          {chatBubbleIcon}
        </div>
      )}
      <button className="chat-widget-toggle" onClick={toggleWidget}>
        {isOpen ? closeIcon : null}
      </button>
    </div>
  );
};

export default ChatWidget;
