import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './ChatWidget.css';

const ChatWidget = ({ apiKey }) => {
  const [messages, setMessages] = useState([
    {
      id: 'welcome-msg',
      content: "How can I help you today?",
      sender: 'bot',
      timestamp: new Date(),
      read: true
    }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isOpen, setIsOpen] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [lastTypingTime, setLastTypingTime] = useState(null);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    
    // Mark all bot messages as read when user views them
    if (isOpen) {
      setMessages(prevMessages => prevMessages.map(msg => 
        msg.sender === 'bot' && !msg.read ? {...msg, read: true} : msg
      ));
    }
  }, [messages, isOpen]);

  // Focus input when chat opens
  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  // Typing indicator timeout
  useEffect(() => {
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }
    
    if (isTyping) {
      typingTimeoutRef.current = setTimeout(() => {
        setIsTyping(false);
      }, 2000);
    }
    
    return () => {
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }
    };
  }, [isTyping]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
    
    // Detect user typing for real-time feedback
    const currentTime = new Date().getTime();
    
    // Only show typing indicator if user hasn't typed for 500ms
    if (!isTyping || (lastTypingTime && currentTime - lastTypingTime > 500)) {
      setIsTyping(true);
      setLastTypingTime(currentTime);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!input.trim()) return;
    
    const messageId = `user-${Date.now()}`;
    const currentTime = new Date();
    
    const userMessage = {
      id: messageId,
      content: input,
      sender: 'user',
      timestamp: currentTime,
      read: true
    };
    
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);
    setIsTyping(false);
    
    try {
      // Send message to backend
      const response = await axios.post('/chat', {
        message: input,
        api_key: apiKey
      });
      
      // Add small random delay to make it feel more natural (300-1200ms)
      const replyDelay = 300 + Math.random() * 900;
      
      // Simulate "typing" delay based on response length
      const typingDuration = Math.min(
        1000 + (response.data.response.length / 10) * 100, 
        3000
      );
      
      // Show typing indicator for a realistic duration
      await new Promise(resolve => setTimeout(resolve, replyDuration = typingDuration));
      
      const botMessage = {
        id: `bot-${Date.now()}`,
        content: response.data.response,
        sender: 'bot',
        timestamp: new Date(),
        read: isOpen // Only mark as read if chat is open
      };
      
      setMessages(prev => [...prev, botMessage]);
      
      // If widget is closed when response arrives, show notification
      if (!isOpen) {
        showNotification("New message from AI Assistant");
      }
    } catch (error) {
      console.error('Error sending message:', error);
      
      const errorMessage = {
        id: `error-${Date.now()}`,
        content: `Sorry, there was an error: ${error.response?.data?.detail || error.message || 'Unknown error'}`,
        sender: 'bot',
        timestamp: new Date(),
        read: isOpen,
        isError: true
      };
      
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const toggleWidget = () => {
    setIsOpen(!isOpen);
    
    // If opening the widget, mark all messages as read
    if (!isOpen) {
      setMessages(prevMessages => prevMessages.map(msg => 
        msg.sender === 'bot' && !msg.read ? {...msg, read: true} : msg
      ));
    }
  };
  
  const showNotification = (message) => {
    // Show browser notification if supported
    if ("Notification" in window) {
      if (Notification.permission === "granted") {
        new Notification(message);
      } else if (Notification.permission !== "denied") {
        Notification.requestPermission().then(permission => {
          if (permission === "granted") {
            new Notification(message);
          }
        });
      }
    }
  };
  
  // Count unread messages
  const unreadCount = messages.filter(msg => msg.sender === 'bot' && !msg.read).length;

  return (
    <div className={`chat-widget ${isOpen ? 'open' : 'closed'}`}>
      {isOpen ? (
        <>
          <div className="chat-widget-header">
            <h3>Chat with AI Assistant</h3>
          </div>
          <div className="chat-widget-messages">
            {messages.map((message) => (
              <div 
                key={message.id} 
                className={`chat-widget-message ${message.sender === 'user' ? 'user-message' : 'bot-message'}`}
              >
                <div className={`chat-widget-message-content ${message.isError ? 'error-message' : ''}`}>
                  {message.content}
                  <span className="message-timestamp">
                    {formatTime(message.timestamp)}
                  </span>
                  {message.sender === 'user' && message.read && (
                    <span className="read-receipt">âœ“</span>
                  )}
                </div>
              </div>
            ))}
            
            {isTyping && (
              <div className="user-typing-indicator">
                <span>You are typing...</span>
              </div>
            )}
            
            {isLoading && (
              <div className="chat-widget-message bot-message">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            )}
            
            <div ref={messagesEndRef} />
          </div>
          <form className="chat-widget-input" onSubmit={handleSubmit}>
            <input
              ref={inputRef}
              type="text"
              value={input}
              onChange={handleInputChange}
              placeholder="Type your message..."
              disabled={isLoading}
            />
            <button type="submit" disabled={isLoading || !input.trim()}>
              <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M22 2L11 13" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M22 2L15 22L11 13L2 9L22 2Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </button>
          </form>
        </>
      ) : (
        <div className="chat-widget-button">
          <span>Chat</span>
          {unreadCount > 0 && (
            <div className="unread-badge">{unreadCount}</div>
          )}
        </div>
      )}
      <button className="chat-widget-toggle" onClick={toggleWidget}>
        {isOpen ? (
          <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M18 6L6 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M6 6L18 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        ) : (
          <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M20 11H4C3.45 11 3 11.45 3 12C3 12.55 3.45 13 4 13H20C20.55 13 21 12.55 21 12C21 11.45 20.55 11 20 11Z" fill="currentColor"/>
          </svg>
        )}
      </button>
    </div>
  );
};

// Helper function to format time for messages
const formatTime = (date) => {
  if (!date) return '';
  const hours = date.getHours();
  const minutes = date.getMinutes();
  return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
};

export default ChatWidget;
